import React, { Component } from 'react';import ReactDOM from 'react-dom';import { BrowserRouter as Router, Route} from 'react-router-dom';import PropTypes from 'prop-types';class Main extends Component {	constructor(props) {		super(props);	}	render() {		return (			<div>				{this.props.children}			</div>		)	}}class Home extends Component {	constructor(props) {		super(props);	}	static contextTypes = {		router: PropTypes.object	};	render() {		return (			<div onClick={() => {this.context.router.history.push('mine/123')}}>Home</div>		)	}}class More extends Component {	constructor(props) {		super(props);	}	static contextTypes = {		router: PropTypes.object	};	render() {		return (			<div onClick={() => {this.context.router.history.push('/')}}>More</div>		)	}}class Mine extends Component {	constructor(props) {		super(props);	}	static contextTypes = {		router: PropTypes.object	};	render() {		return (			<div onClick={() => {this.context.router.history.push('/more')}}>Mine</div>		)	}	componentDidMount() {		// 接受路由传参		console.log(this.props.match.params.id)	}}const AppRouter = (	<Router>		<div>			<Route exact path="/" component={Home}/>			<Route exact path="/mine/:id" component={Mine}/>			<Route exact path="/more" component={More}/>		</div>	</Router>);ReactDOM.render( AppRouter, document.getElementById('app'));